package com.assignments;

import java.util.*;
public class UserInput {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Scanner sc = new Scanner(System.in);
		int n= sc.nextInt();
		int m= sc.nextInt();
		System.out.println( "add of two number "+(n+m));
		System.out.println( "mul of two number "+(n*m));
		System.out.println( "div of two number "+(n/m));
	}

}
